import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 40.1-28.7:1 Genetic testing or genetic characteristics as a condition of employment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-28.7:1.md","filePath":"vacode/40.1-28.7:1.md"}'),a={name:"vacode/40.1-28.7:1.md"},n=o('<h1 id="§-40-1-28-7-1-genetic-testing-or-genetic-characteristics-as-a-condition-of-employment" tabindex="-1">§ 40.1-28.7:1 Genetic testing or genetic characteristics as a condition of employment. <a class="header-anchor" href="#§-40-1-28-7-1-genetic-testing-or-genetic-characteristics-as-a-condition-of-employment" aria-label="Permalink to &quot;§ 40.1-28.7:1 Genetic testing or genetic characteristics as a condition of employment.&quot;">​</a></h1><p>A. No employer shall:</p><ol><li><p>Request, require, solicit or administer a genetic test, as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-508.4/" target="_blank" rel="noreferrer">38.2-508.4</a>, to any person as a condition of employment; or</p></li><li><p>Refuse to hire, fail to promote, discharge or otherwise adversely affect any terms or conditions of employment of any employee or prospective employee solely on the basis of a genetic characteristic, as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-508.4/" target="_blank" rel="noreferrer">38.2-508.4</a>, or the results of a genetic test, regardless of how the employer obtained such information or results. Nothing in this section shall preclude the use of information related to a criminal investigation.</p></li></ol><p>B. The employee may bring an action in a court of competent jurisdiction over the employer who took adverse action against the employee in violation of this section. Any such action shall be brought within 180 days from the date of the adverse action. The court may, in its discretion, award actual or punitive damages, including back pay with interest at the judgment rate as provided in § <a href="http://law.lis.virginia.gov/vacode/6.2-302/" target="_blank" rel="noreferrer">6.2-302</a>, or injunctive relief.</p><p>C. Nothing in this section shall be construed to require the Department of Labor and Industry to conduct any investigations or enforcement actions.</p><p>D. As used in subdivision A 2 of this section, &quot;terms and conditions of employment&quot; shall not include any long term care, life or disability insurance policy.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0659" target="_blank" rel="noreferrer">659</a>.</p>',7),r=[n];function s(c,l,d,h,p,f){return t(),i("div",null,r)}const u=e(a,[["render",s]]);export{g as __pageData,u as default};
