import{_ as t,o as i,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 40.1-28 Unlawful to require payment for medical examination as condition of employment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-28.md","filePath":"vacode/40.1-28.md"}'),n={name:"vacode/40.1-28.md"},r=e("h1",{id:"§-40-1-28-unlawful-to-require-payment-for-medical-examination-as-condition-of-employment",tabindex:"-1"},[o("§ 40.1-28 Unlawful to require payment for medical examination as condition of employment. "),e("a",{class:"header-anchor",href:"#§-40-1-28-unlawful-to-require-payment-for-medical-examination-as-condition-of-employment","aria-label":'Permalink to "§ 40.1-28 Unlawful to require payment for medical examination as condition of employment."'},"​")],-1),l=e("p",null,"It shall be unlawful for any employer to require any employee or applicant for employment to pay the cost of a medical examination or the cost of furnishing any medical records required by the employer as a condition of employment.",-1),s=e("p",null,"Any employer who violates the provisions of this section shall be subject to a civil penalty not to exceed $100 for each violation. The Commissioner shall notify any employer who he alleges has violated any provision of this section by certified mail or overnight delivery service. Such notice shall contain a description of the alleged violation. Within 21 days of receipt of notice of the alleged violation, the employer may request an informal conference regarding such violation with the Commissioner. If the employer fails to contest the violation by requesting such an informal conference within 21 days following its receipt of the notice of the alleged violation, the violation and proposed penalty will become a final order of the Commissioner and not subject to review by any court or agency except upon a showing of good cause. Such informal conference shall result in a decision by the Commissioner that will be appealable to the appropriate circuit court. The Department shall send a copy of the Commissioner's decision to the employer by certified mail or overnight delivery service. The employer may file a notice of an appeal only within 30 days from the receipt of the decision. The appeal shall be on the agency record. With respect to matters of law, the burden shall be on the party seeking review to designate and demonstrate an error of law subject to review by the court. With respect to issues of fact, the duty of the court shall be limited to ascertaining whether there was substantial evidence in the record to reasonably support the Commissioner's findings of fact.",-1),c=e("p",null,"Civil penalties owed under this section shall be paid to the Commissioner for deposit into the general fund of the Treasury of the Commonwealth. The Commissioner shall prescribe procedures for the payment of proposed penalties which are not contested by employers.",-1),h=e("p",null,[o("Code 1950, § 40-22.1; 1952, c. 525; 1962, c. 66; 1970, c. 321; 1973, c. 425; 1982, c. 84; 2015, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0285",target:"_blank",rel:"noreferrer"},"285"),o(".")],-1),m=[r,l,s,c,h];function d(p,f,y,u,v,b){return i(),a("div",null,m)}const w=t(n,[["render",d]]);export{_ as __pageData,w as default};
