import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-33.2 Discriminatory actions prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-33.2.md","filePath":"vacode/40.1-33.2.md"}'),o={name:"vacode/40.1-33.2.md"},n=t('<h1 id="§-40-1-33-2-discriminatory-actions-prohibited" tabindex="-1">§ 40.1-33.2 Discriminatory actions prohibited. <a class="header-anchor" href="#§-40-1-33-2-discriminatory-actions-prohibited" aria-label="Permalink to &quot;§ 40.1-33.2 Discriminatory actions prohibited.&quot;">​</a></h1><p>A. An employer shall not discharge or in any other manner discriminate against an employee because such employee has filed any complaint or instituted or caused to be instituted any proceeding under § <a href="/vacode/40.1-29/">40.1-29</a>, or has testified or is about to testify in any such proceeding.</p><p>B. Any employee who is discharged or in any other manner discriminated against in a manner prohibited by this section may file a complaint with the Commissioner, and the Commissioner, with the written and signed consent of an employee, may institute proceedings on behalf of an employee for appropriate remedies for such action, including reinstatement of the employee and recovering lost wages and an additional amount equal to the lost wages as liquidated damages.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0950" target="_blank" rel="noreferrer">950</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0951" target="_blank" rel="noreferrer">951</a>, § 40.1-33.1.</p>',4),r=[n];function s(c,d,l,h,p,m){return i(),a("div",null,r)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
