import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-49.3 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-49.3.md","filePath":"vacode/40.1-49.3.md"}'),i={name:"vacode/40.1-49.3.md"},r=t('<h1 id="§-40-1-49-3-definitions" tabindex="-1">§ 40.1-49.3 Definitions. <a class="header-anchor" href="#§-40-1-49-3-definitions" aria-label="Permalink to &quot;§ 40.1-49.3 Definitions.&quot;">​</a></h1><p>For the purposes of §§ <a href="/vacode/40.1-49.4/">40.1-49.4</a>, <a href="/vacode/40.1-49.5/">40.1-49.5</a>, <a href="/vacode/40.1-49.6/">40.1-49.6</a>, <a href="/vacode/40.1-49.7/">40.1-49.7</a>, and <a href="/vacode/40.1-51.1/">40.1-51.1</a> through <a href="/vacode/40.1-51.3/">40.1-51.3</a> the following terms shall have the following meanings:</p><p>&quot;Commission&quot; means the Virginia Workers&#39; Compensation Commission.</p><p>&quot;Commissioner&quot; means the Commissioner of Labor and Industry. Except where the context clearly indicates the contrary, any reference to Commissioner shall include his authorized representatives.</p><p>&quot;Employee&quot; means an individual who is employed by an employer.</p><p>&quot;Employer&quot; means any person that (i) is engaged in business or engages an individual to perform domestic service and (ii) has employees. &quot;Employer&quot; does not include the United States.</p><p>&quot;Occupational safety and health standard&quot; means a standard which requires conditions, or the adoption or use of one or more practices, means, methods, operations, or processes, reasonably necessary or appropriate to provide safe or healthful employment and places of employment.</p><p>&quot;Serious violation&quot; means a violation deemed to exist in a place of employment if there is a substantial probability that death or serious physical harm could result from a condition which exists, or from one or more practices, means, methods, operations, or processes which have been adopted or are in use, in such place of employment unless the employer did not, and could not with the exercise of reasonable diligence, know of the presence of the violation.</p><p>&quot;Person&quot; means one or more individuals, partnerships, associations, corporations, business trusts, legal representatives, or any organized group of persons.</p><p>&quot;Circuit court&quot; means the circuit court of the city or county wherein the violation of this title or any standard, rule or regulation issued pursuant thereto is alleged to have occurred. Venue shall be determined in accordance with the provisions of §§ <a href="/vacode/8.01-257/">8.01-257</a> through <a href="/vacode/8.01-267/">8.01-267</a>.</p><p>1979, c. 354; 1992, c. 777; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0509" target="_blank" rel="noreferrer">509</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0513" target="_blank" rel="noreferrer">513</a>.</p>',11),n=[r];function s(c,p,d,h,l,u){return o(),a("div",null,n)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
