import{_ as o,o as i,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 40.1-51.12 Violation for operating boiler or pressure vessel without inspection certificate; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-51.12.md","filePath":"vacode/40.1-51.12.md"}'),a={name:"vacode/40.1-51.12.md"},s=e("h1",{id:"§-40-1-51-12-violation-for-operating-boiler-or-pressure-vessel-without-inspection-certificate-civil-penalty",tabindex:"-1"},[t("§ 40.1-51.12 Violation for operating boiler or pressure vessel without inspection certificate; civil penalty. "),e("a",{class:"header-anchor",href:"#§-40-1-51-12-violation-for-operating-boiler-or-pressure-vessel-without-inspection-certificate-civil-penalty","aria-label":'Permalink to "§ 40.1-51.12 Violation for operating boiler or pressure vessel without inspection certificate; civil penalty."'},"​")],-1),n=e("p",null,"A. After twelve months following July 1, 1972, it shall be unlawful for any person, firm, partnership or corporation to operate in this Commonwealth a boiler or pressure vessel without a valid inspection certificate. Any owner, user, operator or agent of any such person who actually operates or is responsible for operating such boiler or pressure vessel thereof who operates a boiler or pressure vessel without such inspection certificate, or at a pressure exceeding that specified in such inspection certificate shall be in violation of this section and subject to a civil penalty not to exceed $100. Each day of such violation shall be deemed a separate offense.",-1),l=e("p",null,[t("B. All procedural rights guaranteed to employers pursuant to § "),e("a",{href:"http://law.lis.virginia.gov/vacode/40.1-49.4/",target:"_blank",rel:"noreferrer"},"40.1-49.4"),t(" shall apply to penalties under this section.")],-1),c=e("p",null,"C. Investigation and enforcement for violations of this section shall be carried out by the Department of Labor and Industry. Civil penalties imposed for violations of this section shall be paid into the general fund.",-1),p=e("p",null,[t("1972, c. 237; 1995, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0097",target:"_blank",rel:"noreferrer"},"97"),t(".")],-1),h=[s,n,l,c,p];function f(u,d,v,_,b,g){return i(),r("div",null,h)}const w=o(a,[["render",f]]);export{y as __pageData,w as default};
