import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-51.19:4 Operations and maintenance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-51.19:4.md","filePath":"vacode/40.1-51.19:4.md"}'),n={name:"vacode/40.1-51.19:4.md"},r=t('<h1 id="§-40-1-51-19-4-operations-and-maintenance" tabindex="-1">§ 40.1-51.19:4 Operations and maintenance. <a class="header-anchor" href="#§-40-1-51-19-4-operations-and-maintenance" aria-label="Permalink to &quot;§ 40.1-51.19:4 Operations and maintenance.&quot;">​</a></h1><p>A. A hobby or model boiler must be attended by a person reasonably competent to operate such boiler when in operation. For the purposes of this section, a hobby or model boiler may be considered as not being in operation when all of the following conditions exist:</p><ol><li><p>The water level is at least one-third of the water gauge glass;</p></li><li><p>The fire is banked and the draft doors closed or the fire is extinguished; and</p></li><li><p>The boiler pressure is at least twenty pounds per square inch below the lowest safety valve set pressure.</p></li></ol><p>B. All welding performed on hobby or model boilers shall be done by an &quot;R&quot; stamp holder in accordance with the inspection code of the National Board of Boiler and Pressure Vessel Inspectors.</p><p>C. Repairs to longitudinal riveted joints are prohibited.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0335" target="_blank" rel="noreferrer">335</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0898" target="_blank" rel="noreferrer">898</a>.</p>',6),i=[r];function s(l,d,p,c,h,_){return a(),o("div",null,i)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
