import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-51.19:5 Civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-51.19:5.md","filePath":"vacode/40.1-51.19:5.md"}'),r={name:"vacode/40.1-51.19:5.md"},i=o('<h1 id="§-40-1-51-19-5-civil-penalty" tabindex="-1">§ 40.1-51.19:5 Civil penalty. <a class="header-anchor" href="#§-40-1-51-19-5-civil-penalty" aria-label="Permalink to &quot;§ 40.1-51.19:5 Civil penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person, firm, partnership or corporation to operate in the Commonwealth a hobby or model boiler without a valid certificate. Any such person shall be subject to a civil penalty as provided by § <a href="http://law.lis.virginia.gov/vacode/40.1-51.12/" target="_blank" rel="noreferrer">40.1-51.12</a>.</p><p>B. Any owner or user who leaves or causes to leave a hobby or model boiler unattended while in operation at an event to which members of the general public are invited shall be in violation of this article and subject to a civil penalty not to exceed $5,000. Each instance of such violation shall be deemed a separate offense.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0335" target="_blank" rel="noreferrer">335</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0898" target="_blank" rel="noreferrer">898</a>.</p>',4),n=[i];function l(c,s,p,h,_,d){return a(),t("div",null,n)}const b=e(r,[["render",l]]);export{f as __pageData,b as default};
