import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-51.26 Commissioner of Labor and Industry to enforce laws.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-51.26.md","filePath":"vacode/40.1-51.26.md"}'),t={name:"vacode/40.1-51.26.md"},n=a('<h1 id="§-40-1-51-26-commissioner-of-labor-and-industry-to-enforce-laws" tabindex="-1">§ 40.1-51.26 Commissioner of Labor and Industry to enforce laws. <a class="header-anchor" href="#§-40-1-51-26-commissioner-of-labor-and-industry-to-enforce-laws" aria-label="Permalink to &quot;§ 40.1-51.26 Commissioner of Labor and Industry to enforce laws.&quot;">​</a></h1><p>The Commissioner of Labor and Industry shall have the authority to:</p><ol><li><p>Supervise, administer, and enforce the provisions of this chapter and regulations of the Board;</p></li><li><p>Receive complaints as to asbestos NESHAP violations;</p></li><li><p>Hold or cause to be held hearings and enter orders diminishing or abating the causes of air pollution and orders to enforce regulations pursuant to § <a href="http://law.lis.virginia.gov/vacode/40.1-51.28/" target="_blank" rel="noreferrer">40.1-51.28</a>;</p></li><li><p>Institute legal proceedings, including suits for injunctions for the enforcement of his orders, regulations of the Board, and for the enforcement of penalties;</p></li><li><p>Investigate any violations of this chapter and regulations;</p></li><li><p>Require that asbestos NESHAP records and reports be made available upon request, and require owners to develop, maintain, and make available such other records and information as are deemed necessary for the proper enforcement of this chapter and regulation; and</p></li><li><p>Upon presenting appropriate credentials to the owner, operator, or agent in charge:</p></li></ol><p>a. Enter without delay and at reasonable times any business establishment, construction site, or other area, workplace, or environment in this Commonwealth, subject to federal security requirements; and</p><p>b. Inspect and investigate during regular working hours and at other reasonable times, and within reasonable limits and in a reasonable manner, without prior notice, unless such notice is authorized by the Commissioner or his representative, any such business establishment or place of employment and all pertinent conditions, structures, machines, apparatus, devices, equipment, and materials therein, and question privately any such employer, officer, owner, operator, agent, or employee. If such entry or inspection is refused, prohibited, or otherwise interfered with, the Commissioner shall have the power to seek an order compelling such entry or inspection, pursuant to § <a href="http://law.lis.virginia.gov/vacode/40.1-49.9/" target="_blank" rel="noreferrer">40.1-49.9</a>.</p><p>1992, c. 541.</p>',6),i=[n];function s(l,d,c,p,h,u){return o(),r("div",null,i)}const _=e(t,[["render",s]]);export{f as __pageData,_ as default};
