import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 40.1-51.9:1 Examination of inspectors; certificate of competency required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-51.9:1.md","filePath":"vacode/40.1-51.9:1.md"}'),o={name:"vacode/40.1-51.9:1.md"},r=i('<h1 id="§-40-1-51-9-1-examination-of-inspectors-certificate-of-competency-required" tabindex="-1">§ 40.1-51.9:1 Examination of inspectors; certificate of competency required. <a class="header-anchor" href="#§-40-1-51-9-1-examination-of-inspectors-certificate-of-competency-required" aria-label="Permalink to &quot;§ 40.1-51.9:1 Examination of inspectors; certificate of competency required.&quot;">​</a></h1><p>A. All applicants for the position of inspector authorized by § <a href="http://law.lis.virginia.gov/vacode/40.1-51.9/" target="_blank" rel="noreferrer">40.1-51.9</a> shall be required to have successfully completed an examination monitored by the Examining Board and to have received a certificate of competency from the Commissioner prior to commencing their duties. A fee as set under subsection A of § <a href="http://law.lis.virginia.gov/vacode/40.1-51.15/" target="_blank" rel="noreferrer">40.1-51.15</a> shall be charged each applicant taking the inspector&#39;s examination.</p><p>B. Each inspector holding a valid certificate of competency and who conducts inspections, as provided by this chapter, shall be required to obtain an identification card biennially, not later than June 30 of the year in which the identification card is required. Application for the identification card shall be made on forms furnished by the Department upon request. Each application shall be submitted to the Department, accompanied by a post-office money order or check drawn to the order of the Treasurer of Virginia in the amount as set under subsection A of § <a href="http://law.lis.virginia.gov/vacode/40.1-51.15/" target="_blank" rel="noreferrer">40.1-51.15</a>.</p><p>1974, c. 195; 1986, c. 266; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0212" target="_blank" rel="noreferrer">212</a>.</p>',4),n=[r];function c(s,d,p,f,l,h){return t(),a("div",null,n)}const u=e(o,[["render",c]]);export{_ as __pageData,u as default};
