import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 40.1-55 Employee striking terminates, and becomes temporarily ineligible for, public employment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-55.md","filePath":"vacode/40.1-55.md"}'),n={name:"vacode/40.1-55.md"},r=i('<h1 id="§-40-1-55-employee-striking-terminates-and-becomes-temporarily-ineligible-for-public-employment" tabindex="-1">§ 40.1-55 Employee striking terminates, and becomes temporarily ineligible for, public employment. <a class="header-anchor" href="#§-40-1-55-employee-striking-terminates-and-becomes-temporarily-ineligible-for-public-employment" aria-label="Permalink to &quot;§ 40.1-55 Employee striking terminates, and becomes temporarily ineligible for, public employment.&quot;">​</a></h1><p>A. Any employee of the Commonwealth, or of any county, city, town or other political subdivision thereof, or of any agency of any one of them, who, in concert with two or more other such employees, for the purpose of obstructing, impeding or suspending any activity or operation of his employing agency or any other governmental agency, strikes or willfully refuses to perform the duties of his employment shall, by such action, be deemed to have terminated his employment and shall thereafter be ineligible for employment in any position or capacity during the next 12 months by the Commonwealth, or any county, city, town or other political subdivision of the Commonwealth, or by any department or agency of any of them.</p><p>B. The provisions of subsection A shall apply to any employee of any county, city, or town or local school board without regard to any local ordinance or resolution adopted pursuant to § <a href="/vacode/40.1-57.2/">40.1-57.2</a> by such county, city, or town or school board that authorizes its employees to engage in collective bargaining.</p><p>Code 1950, § 40-65; 1970, c. 321; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1209" target="_blank" rel="noreferrer">1209</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1276" target="_blank" rel="noreferrer">1276</a>.</p>',4),a=[r];function l(s,c,m,p,y,h){return o(),t("div",null,a)}const g=e(n,[["render",l]]);export{f as __pageData,g as default};
