import{_ as t,o,c as a,z as e,a as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 40.1-57.1 Appeal by employer for reemployment of terminated employee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-57.1.md","filePath":"vacode/40.1-57.1.md"}'),n={name:"vacode/40.1-57.1.md"},i=e("h1",{id:"§-40-1-57-1-appeal-by-employer-for-reemployment-of-terminated-employee",tabindex:"-1"},[r("§ 40.1-57.1 Appeal by employer for reemployment of terminated employee. "),e("a",{class:"header-anchor",href:"#§-40-1-57-1-appeal-by-employer-for-reemployment-of-terminated-employee","aria-label":'Permalink to "§ 40.1-57.1 Appeal by employer for reemployment of terminated employee."'},"​")],-1),l=e("p",null,"Notwithstanding any provision of law to the contrary, in the event that the employer of an individual terminated under this article deems it necessary for the protection of the public welfare that such individual be reemployed within the twelve months following his termination, the employer may, within ninety days after the date of the declaration of ineligibility, appeal to the circuit court of the county or the circuit court of the city in which the individual was employed by filing a petition therein setting forth the reasons why the public welfare requires reemployment. A copy of the petition shall be served upon or sent by registered mail to the former employee, who may file an answer therein ten days after receiving the same. The court or the judge thereof in vacation shall notify the employer and former employee of the time and place of the hearing on the appeal, such hearing to be de novo and to be held as promptly as possible. The court shall hear such testimony as may be adduced by the respective parties and render judgment in accordance with the law and the evidence. Such judgment shall be final.",-1),h=e("p",null,"1972, c. 792.",-1),p=[i,l,h];function s(d,m,c,y,f,u){return o(),a("div",null,p)}const v=t(n,[["render",s]]);expor