import{_ as t,o as a,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 40.1-57 Appeal by employee from declaration of ineligibility.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-57.md","filePath":"vacode/40.1-57.md"}'),n={name:"vacode/40.1-57.md"},l=e("h1",{id:"§-40-1-57-appeal-by-employee-from-declaration-of-ineligibility",tabindex:"-1"},[o("§ 40.1-57 Appeal by employee from declaration of ineligibility. "),e("a",{class:"header-anchor",href:"#§-40-1-57-appeal-by-employee-from-declaration-of-ineligibility","aria-label":'Permalink to "§ 40.1-57 Appeal by employee from declaration of ineligibility."'},"​")],-1),r=e("p",null,"In the event that any such employee feels aggrieved by such declaration of ineligibility he may within ninety days after the date thereof appeal to the circuit court of the county or the circuit court of the city in which he was employed by filing a petition therein for a review of the matters of law and fact involved in or pertinent to the declaration of ineligibility. A copy of the petition shall be served upon or sent by registered mail to the official signing the declaration, who may file an answer thereto within ten days after receiving the same. The court or the judge thereof in vacation shall, as promptly as practicable, hear the appeal de novo and notify the employee and the signer of the declaration of ineligibility of the time and place of hearing. The court shall hear such testimony as may be adduced by the respective parties and render judgment in accordance with the law and the evidence. Such judgment shall be final.",-1),c=e("p",null,"Code 1950, § 40-67; 1970, c. 321.",-1),h=[l,r,c];function d(s,p,f,y,m,b){return a(),i("div",null,h)}const _=t(n,[["render",d]]);export{g as __pageData,_ as default};
