import{_ as e,o as t,c as r,z as o,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 40.1-68 Service of process on clerk of State Corporation Commission as attorney for union.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-68.md","filePath":"vacode/40.1-68.md"}'),n={name:"vacode/40.1-68.md"},s=o("h1",{id:"§-40-1-68-service-of-process-on-clerk-of-state-corporation-commission-as-attorney-for-union",tabindex:"-1"},[a("§ 40.1-68 Service of process on clerk of State Corporation Commission as attorney for union. "),o("a",{class:"header-anchor",href:"#§-40-1-68-service-of-process-on-clerk-of-state-corporation-commission-as-attorney-for-union","aria-label":'Permalink to "§ 40.1-68 Service of process on clerk of State Corporation Commission as attorney for union."'},"​")],-1),i=o("p",null,"Any labor union or labor organization doing business in this Commonwealth, all of whose officers and trustees are nonresidents of this Commonwealth, shall by written power of attorney, filed with the Department of Labor and Industry and the State Corporation Commission, appoint the clerk of the State Corporation Commission its attorney or agent upon whom all legal process against the union or organization may be served, and who shall be authorized to enter an appearance on its behalf. The manner of service of process on the clerk of the State Corporation Commission, the mailing thereof to the labor union or organization, the fees therefor, the effect of judgments, decrees and orders, and the procedure in cases where no power of attorney is filed as required, shall be the same as provided for in cases of foreign corporations.",-1),c=o("p",null,"Code 1950, § 40-74.4; 1954, c. 431; 1956, c. 430; 1970, c. 321.",-1),f=[s,i,c];function l(d,h,p,m,u,_){return t(),r("div",null,f)}const b=e(n,[["render",l]]);export{v as __pageData,b as default};
