import{_ as e,o as r,c as a,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 40.1-78 Employment of children under fourteen and sixteen.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-78.md","filePath":"vacode/40.1-78.md"}'),o={name:"vacode/40.1-78.md"},t=n('<h1 id="§-40-1-78-employment-of-children-under-fourteen-and-sixteen" tabindex="-1">§ 40.1-78 Employment of children under fourteen and sixteen. <a class="header-anchor" href="#§-40-1-78-employment-of-children-under-fourteen-and-sixteen" aria-label="Permalink to &quot;§ 40.1-78 Employment of children under fourteen and sixteen.&quot;">​</a></h1><p>A. No child under fourteen years of age shall be employed, permitted or suffered to work in, about or in connection with any gainful occupation except as specified in this chapter.</p><p>B. No child under sixteen years of age shall be employed, permitted or suffered to work in, about or in connection with any gainful occupation during school hours unless he has reached the age of fourteen and is enrolled in a regular school work-training program and a work-training certificate has been issued for his employment as provided in § <a href="http://law.lis.virginia.gov/vacode/40.1-88/" target="_blank" rel="noreferrer">40.1-88</a>.</p><p>C. Nothing in this section shall affect the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/40.1-100/" target="_blank" rel="noreferrer">40.1-100</a> A, <a href="http://law.lis.virginia.gov/vacode/40.1-100.1/" target="_blank" rel="noreferrer">40.1-100.1</a>, <a href="http://law.lis.virginia.gov/vacode/40.1-100.2/" target="_blank" rel="noreferrer">40.1-100.2</a>, <a href="http://law.lis.virginia.gov/vacode/40.1-101/" target="_blank" rel="noreferrer">40.1-101</a> and <a href="http://law.lis.virginia.gov/vacode/40.1-102/" target="_blank" rel="noreferrer">40.1-102</a>.</p><p>Code 1950, § 40-96; 1956, c. 567; 1960, c. 434; 1968, c. 264; 1970, c. 321; 1991, c. 511.</p>',5),i=[t];fun