import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 40.1-80.1 Employment of children.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-80.1.md","filePath":"vacode/40.1-80.1.md"}'),a={name:"vacode/40.1-80.1.md"},n=r('<h1 id="§-40-1-80-1-employment-of-children" tabindex="-1">§ 40.1-80.1 Employment of children. <a class="header-anchor" href="#§-40-1-80-1-employment-of-children" aria-label="Permalink to &quot;§ 40.1-80.1 Employment of children.&quot;">​</a></h1><p>A. Except as provided in §§ <a href="http://law.lis.virginia.gov/vacode/40.1-79.01/" target="_blank" rel="noreferrer">40.1-79.01</a>, <a href="http://law.lis.virginia.gov/vacode/40.1-88/" target="_blank" rel="noreferrer">40.1-88</a>, <a href="http://law.lis.virginia.gov/vacode/40.1-102/" target="_blank" rel="noreferrer">40.1-102</a>, and <a href="http://law.lis.virginia.gov/vacode/40.1-109/" target="_blank" rel="noreferrer">40.1-109</a>, no child under sixteen years of age shall be employed, permitted or suffered to work in, about or in connection with any gainful occupation more than the number of hours per week or more than the number of hours per day or during the hours of the day that the Commissioner shall determine by regulations to be detrimental to the lives, health, safety or welfare of children. These regulations shall incorporate the standards contained in regulations promulgated by the United States Secretary of Labor pursuant to the Fair Labor Standards Act (29 U.S.C. § 201 et seq.) concerning the number of hours per week, hours per day, and the hours of the day that children under the age of sixteen may work in, about, or in connection with, any gainful occupation.</p><p>B. No child shall be employed or permitted to work for more than five hours continuously without an interval of at least thirty minutes for a lunch period, and no period of less than thirty minutes shall be deemed to interrupt a continuous period of work.</p><p>1991, c. 511.</p>',4),i=[n];function l(h,s,d,c,p,f){return t(),o("div",null,i)}const _=e(a,[["render",l]]);export{m as __pageData,_ as default};
