import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 40.1-84 Employment certificate required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-84.md","filePath":"vacode/40.1-84.md"}'),o={name:"vacode/40.1-84.md"},i=a('<h1 id="§-40-1-84-employment-certificate-required" tabindex="-1">§ 40.1-84 Employment certificate required. <a class="header-anchor" href="#§-40-1-84-employment-certificate-required" aria-label="Permalink to &quot;§ 40.1-84 Employment certificate required.&quot;">​</a></h1><p>No child under sixteen years of age shall be employed, permitted or suffered to work, in, about or in connection with any gainful occupation with the exception of volunteer work or work on farms, orchards and in gardens and except as provided in §§ <a href="http://law.lis.virginia.gov/vacode/40.1-79.01/" target="_blank" rel="noreferrer">40.1-79.01</a>, <a href="http://law.lis.virginia.gov/vacode/40.1-101/" target="_blank" rel="noreferrer">40.1-101</a>, and <a href="http://law.lis.virginia.gov/vacode/40.1-102/" target="_blank" rel="noreferrer">40.1-102</a> unless the person, firm or corporation employing such child, procures and keeps on file and accessible to any school attendance officer, representative of the Department or other authorized persons, charged with the enforcement of this chapter, the employment certificate as hereinafter provided, issued for such child.</p><p>Code 1950, § 40-100; 1960, c. 434; 1966, c. 603; 1970, c. 321; 1972, cc. 480, 824; 1974, cc. 283, 525; 1979, c. 219; 1991, c. 511.</p>',3),n=[i];function c(s,d,l,p,h,f){return r(),t("div",null,n)}const u=e(o,[["render",c]]);export{m as __pageData,u as default};
