import{_ as t,o as a,c as n,z as e,a as o}from"./chunks/framework.9482e208.js";const w=JSON.parse(`{"title":"§ 41.1-8 When grant invalid; when Commonwealth's right relinquished to land settled on.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/41.1-8.md","filePath":"vacode/41.1-8.md"}`),s={name:"vacode/41.1-8.md"},h=e("h1",{id:"§-41-1-8-when-grant-invalid-when-commonwealth-s-right-relinquished-to-land-settled-on",tabindex:"-1"},[o("§ 41.1-8 When grant invalid; when Commonwealth's right relinquished to land settled on. "),e("a",{class:"header-anchor",href:"#§-41-1-8-when-grant-invalid-when-commonwealth-s-right-relinquished-to-land-settled-on","aria-label":`Permalink to "§ 41.1-8 When grant invalid; when Commonwealth's right relinquished to land settled on."`},"​")],-1),r=e("p",null,"No grant of any land which shall have been settled continuously for five years previously, upon which taxes shall have been paid at any time within such five years by the person having settled the same, or any person claiming under him, shall be valid; and any title which the Commonwealth may have to such land shall be relinquished to the person in possession of the land claiming the same under such settlement and payment to the extent of the boundary line enclosing the same. But such boundary line shall not include more than 1,500 acres; and any person who has made such settlement and paid such taxes, or anyone claiming under him, may have the land surveyed, and prove the settlement and payment before the circuit court of the county where the land, or a greater part thereof, lies, whereupon such court shall order the plat and certificate of survey to be recorded. Such record shall be conclusive evidence in any controversy between the claimant thereunder and any person claiming under a location of the land made after the date of such order. This section shall relate as well to land forfeited for nonpayment of taxes, or for the failure to have the same entered on the commissioner's books, or both those causes, and to land escheated or escheatable, as to waste and unappropriated lands.",-1),l=e("p",null,"Code 1950, § 41-39; 1970, c. 291.",-1),i=[h,r,l];function d(c,m,u,p,f,v){return a(),n("div",null,i)}const _=t(s,[["render",d]]);export{w as __pageData,_ as default};
