import{_ as i,o as e,c as a,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 42.1-15.1 Qualifications required to hold professional librarian position.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/42.1-15.1.md","filePath":"vacode/42.1-15.1.md"}'),r={name:"vacode/42.1-15.1.md"},t=o('<h1 id="§-42-1-15-1-qualifications-required-to-hold-professional-librarian-position" tabindex="-1">§ 42.1-15.1 Qualifications required to hold professional librarian position. <a class="header-anchor" href="#§-42-1-15-1-qualifications-required-to-hold-professional-librarian-position" aria-label="Permalink to &quot;§ 42.1-15.1 Qualifications required to hold professional librarian position.&quot;">​</a></h1><p>Public libraries serving a political subdivision or subdivisions having a population greater than 15,000 and libraries operated by the Commonwealth or under its authority shall not employ, in the position of librarian or in any other full-time professional librarian position, a person who does not meet the qualifications established by the State Library Board.</p><p>A professional librarian position as used in this section is one that requires a knowledge of books and of library technique equivalent to that required for graduation from any accredited library school or one that requires graduation from a school of library science accredited by the American Library Association.</p><p>No funds derived from any state aid shall be paid to any person whose employment does not comply with this section.</p><p>This section shall not apply to law libraries organized pursuant to Chapter 4 (§ <a href="http://law.lis.virginia.gov/vacode/42.1-60/" target="_blank" rel="noreferrer">42.1-60</a> et seq.), libraries in institutions of higher education, or public school libraries.</p><p>1988, c. 716; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0539" target="_blank" rel="noreferrer">539</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>.</p>',6),n=[t];function s(l,c,p,d,h,f){return e(),a("div",null,n)}const _=i(r,[["render",s]]);export{b as __pageData,_ as default};
