import{_ as a,o as e,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 42.1-50 Limitation of grants; proration of funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/42.1-50.md","filePath":"vacode/42.1-50.md"}'),o={name:"vacode/42.1-50.md"},i=r('<h1 id="§-42-1-50-limitation-of-grants-proration-of-funds" tabindex="-1">§ 42.1-50 Limitation of grants; proration of funds. <a class="header-anchor" href="#§-42-1-50-limitation-of-grants-proration-of-funds" aria-label="Permalink to &quot;§ 42.1-50 Limitation of grants; proration of funds.&quot;">​</a></h1><p>The total amount of grants under §§ <a href="http://law.lis.virginia.gov/vacode/42.1-48/" target="_blank" rel="noreferrer">42.1-48</a> and <a href="http://law.lis.virginia.gov/vacode/42.1-49/" target="_blank" rel="noreferrer">42.1-49</a> shall not exceed the amount expended, exclusive of state and federal aid, by the political subdivision or subdivisions operating the library. If the state appropriations provided for grants under §§ <a href="http://law.lis.virginia.gov/vacode/42.1-48/" target="_blank" rel="noreferrer">42.1-48</a> and <a href="http://law.lis.virginia.gov/vacode/42.1-49/" target="_blank" rel="noreferrer">42.1-49</a> are not sufficient to meet approved applications, the Library Board shall prorate the available funds in such manner that each application shall receive its proportionate share of each type of grant. Applications must be received prior to June one of each calendar year.</p><p>Code 1950, § 42-25; 1952, c. 494; 1958, c. 426; 1970, c. 606.</p>',3),n=[i];function s(l,d,c,p,f,h){return e(),t("div",null,n)}const u=a(o,[["render",s]]);export{v as __pageData,u as default};
