import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 43-22 How liens enforced.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/43-22.md","filePath":"vacode/43-22.md"}'),r={name:"vacode/43-22.md"},i=e("h1",{id:"§-43-22-how-liens-enforced",tabindex:"-1"},[a("§ 43-22 How liens enforced. "),e("a",{class:"header-anchor",href:"#§-43-22-how-liens-enforced","aria-label":'Permalink to "§ 43-22 How liens enforced."'},"​")],-1),s=e("p",null,"The liens created and perfected under this chapter may be enforced in a court of equity by a bill filed in the county or city wherein the building, structure, or railroad, or some part thereof is situated, or wherein the owner, or if there be more than one, any of them, resides. The plaintiff shall file with his bill an itemized statement of his account, showing the amount and character of the work done or materials furnished, the prices charged therefor, the payments made, if any, the balance due, and the time from which interest is claimed thereon, the correctness of which account shall be verified by the affidavit of himself, or his agent. When suit is brought for the enforcement of any such lien against the property bound thereby, all parties entitled to such liens upon the property or any portion thereof may file petitions in such suit asking for the enforcement of their respective liens to have the same effect as if an independent suit were brought by each claimant.",-1),h=e("p",null,"Code 1919, § 6437; 1920, p. 485.",-1),c=[i,s,h];function d(l,f,p,u,m,_){return o(),n("div",null,c)}const w=t(r,[["render",d]]);export{y as __pageData,w as default};
