import{_ as t,o,c as a,z as e,a as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 43-66 Purchaser not affected by liens of Mutual Assurance Society against Fire.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/43-66.md","filePath":"vacode/43-66.md"}'),s={name:"vacode/43-66.md"},c=e("h1",{id:"§-43-66-purchaser-not-affected-by-liens-of-mutual-assurance-society-against-fire",tabindex:"-1"},[i("§ 43-66 Purchaser not affected by liens of Mutual Assurance Society against Fire. "),e("a",{class:"header-anchor",href:"#§-43-66-purchaser-not-affected-by-liens-of-mutual-assurance-society-against-fire","aria-label":'Permalink to "§ 43-66 Purchaser not affected by liens of Mutual Assurance Society against Fire."'},"​")],-1),n=e("p",null,`The lien of the Mutual Assurance Society against Fire on buildings in the Commonwealth of Virginia upon property insured therein for quotas assessed against the policies of such property owners shall not be valid against purchasers of such property for valuable consideration without notice of the existence of such insurance, except from the time the Society shall have complied with the provisions of this section. The Society shall have prepared and kept in the clerk's office of the Circuit Court of the City of Richmond, and of the circuit court of each county and the circuit court of each city in which the Society insures property, a book to be called the "Mutual Assurance Society's Lien Book." Such book when left with the clerk shall become a public record and shall be kept open to the inspection of the public. The Society shall have entered in such book a brief description of the property insured by it upon which it claims a lien, the date and amount of the policy and the name of the parties to whom the policy was issued, and the date of the entry shall be made in such book.`,-1),r=e("p",null,"Code 1919, § 6458.",-1),h=[c,n,r];function l(u,d,f,p,y,m){return o(),a("div",null,h)}const k=t(s,[["render",l]]);export{_ as __pageData,k as default};
