import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 44-1 Composition of militia.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-1.md","filePath":"vacode/44-1.md"}'),o={name:"vacode/44-1.md"},r=a('<h1 id="§-44-1-composition-of-militia" tabindex="-1">§ 44-1 Composition of militia. <a class="header-anchor" href="#§-44-1-composition-of-militia" aria-label="Permalink to &quot;§ 44-1 Composition of militia.&quot;">​</a></h1><p>The militia of the Commonwealth of Virginia shall consist of all able-bodied residents of the Commonwealth who are citizens of the United States and all other able-bodied persons resident in the Commonwealth who have declared their intention to become citizens of the United States, who are at least 16 years of age and, except as hereinafter provided, not more than 55 years of age. The militia shall be divided into three classes: the National Guard, which includes the Army National Guard and the Air National Guard; the Virginia Defense Force; and the unorganized militia.</p><p>1930, p. 948; 1942, p. 642; Michie Code 1942, § 2673(1); 1944, p. 24; 1958, c. 393; 1970, c. 662; 1973, c. 401; 1976, c. 399; 1979, c. 647; 1984, c. 765; 1989, c. 414; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>.</p>',3),n=[r];function l(s,c,h,d,f,m){return i(),t("div",null,n)}const g=e(o,[["render",l]]);export{_ as __pageData,g as default};
