import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 44-100 No action allowed on account of military duties; counsel for members sued or prosecuted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-100.md","filePath":"vacode/44-100.md"}'),a={name:"vacode/44-100.md"},i=r('<h1 id="§-44-100-no-action-allowed-on-account-of-military-duties-counsel-for-members-sued-or-prosecuted" tabindex="-1">§ 44-100 No action allowed on account of military duties; counsel for members sued or prosecuted. <a class="header-anchor" href="#§-44-100-no-action-allowed-on-account-of-military-duties-counsel-for-members-sued-or-prosecuted" aria-label="Permalink to &quot;§ 44-100 No action allowed on account of military duties; counsel for members sued or prosecuted.&quot;">​</a></h1><p>No action or proceeding shall be prosecuted or maintained against a member of a military court, or officer or person acting under its authority or reviewing its proceedings, on account of the approval or imposition or execution of any sentence, or the imposition or collection of fine or penalty, or the execution of any warrant, writ, execution, process, or mandate of a military court, nor shall any member of the Virginia National Guard or Virginia Defense Force be liable to civil action or suit or criminal prosecution for any act done while in the discharge of his military duty.</p><p>If any member of the Virginia National Guard or Virginia Defense Force is sued civilly or arrested, indicted, or otherwise prosecuted for any act committed in the discharge of his official duty while on state duty, the Adjutant General may employ special counsel approved by the Attorney General to defend such member. The compensation for special counsel employed pursuant to this section shall, subject to the approval by the Attorney General, be paid out of the funds appropriated for the administration of the Department of Military Affairs.</p><p>1930, p. 967; Michie Code 1942, § 2673(85); 1972, c. 416; 1973, c. 401; 1984, c. 765; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>.</p>',4),n=[i];function c(s,l,d,u,f,p){return o(),t("div",null,n)}const _=e(a,[["render",c]]);export{h as __pageData,_ as default};
