import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 44-102.1 Rights, benefits and protections upon call to active duty; contract termination.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-102.1.md","filePath":"vacode/44-102.1.md"}'),a={name:"vacode/44-102.1.md"},o=r('<h1 id="§-44-102-1-rights-benefits-and-protections-upon-call-to-active-duty-contract-termination" tabindex="-1">§ 44-102.1 Rights, benefits and protections upon call to active duty; contract termination. <a class="header-anchor" href="#§-44-102-1-rights-benefits-and-protections-upon-call-to-active-duty-contract-termination" aria-label="Permalink to &quot;§ 44-102.1 Rights, benefits and protections upon call to active duty; contract termination.&quot;">​</a></h1><p>A. Any right, benefit, or protection that may accrue to a member of the Virginia National Guard under the federal Servicemembers Civil Relief Act (50 U.S.C. § 3901 et seq.), as a result of a call to federal active duty service under Title 10 of the United States Code shall be extended to a member of the Virginia National Guard called to active duty service under Title 32 of the United States Code, or to state active duty by the Governor, if the active duty orders are for a period of 30 consecutive days or more. In addition, if a member of the Virginia National Guard is called to state active duty by the Governor, the employer shall ensure that the member has the option of continuing, at the member&#39;s expense, his health care coverage, life insurance, or long-term care insurance.</p><p>B. Any member of the United States Armed Forces or Virginia National Guard who receives permanent change of station orders or has received temporary duty orders in excess of three months&#39; duration, his spouse, or his dependent may, at any time prior to the conclusion of his period of service, terminate without penalty a contract for the following services:</p><ol><li><p>Telecommunications services;</p></li><li><p>Internet services;</p></li><li><p>Television services;</p></li><li><p>Athletic club or gym memberships;</p></li><li><p>Satellite radio services; or</p></li><li><p>Service for an alarm system as defined in § <a href="http://law.lis.virginia.gov/vacode/15.2-911/" target="_blank" rel="noreferrer">15.2-911</a>.</p></li></ol><p>It shall be a violation of the Virginia Consumer Protection Act (§ <a href="http://law.lis.virginia.gov/vacode/59.1-196/" target="_blank" rel="noreferrer">59.1-196</a> et seq.) for a service provider to fail to comply with the provisions of this subsection.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0769" target="_blank" rel="noreferrer">769</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0811" target="_blank" rel="noreferrer">811</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0293" target="_blank" rel="noreferrer">293</a>.</p>',6),n=[o];function l(s,c,h,d,p,f){return t(),i("div",null,n)}const u=e(a,[["render",l]]);export{g as __pageData,u as default};
