import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 44-102.1:1 Benefits upon call to active duty under a state of emergency; health care premiums.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-102.1:1.md","filePath":"vacode/44-102.1:1.md"}'),i={name:"vacode/44-102.1:1.md"},o=r('<h1 id="§-44-102-1-1-benefits-upon-call-to-active-duty-under-a-state-of-emergency-health-care-premiums" tabindex="-1">§ 44-102.1:1 Benefits upon call to active duty under a state of emergency; health care premiums. <a class="header-anchor" href="#§-44-102-1-1-benefits-upon-call-to-active-duty-under-a-state-of-emergency-health-care-premiums" aria-label="Permalink to &quot;§ 44-102.1:1 Benefits upon call to active duty under a state of emergency; health care premiums.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Department&quot; means the Department of Military Affairs.</p><p>&quot;Service member&quot; means a member of the Virginia National Guard or the Virginia Defense Force.</p><p>&quot;State of emergency&quot; has the same meaning as provided in § <a href="/vacode/44-146.16/">44-146.16</a>.</p><p>B. If the Governor has declared a state of emergency that activated the Virginia National Guard or the Virginia Defense Force, then the Department is authorized to pay, for any service member who has served under such activation for a period of at least 14 consecutive days, the portion of the premium for such service member&#39;s health care coverage previously paid by the service member&#39;s employer, provided that the service member provides satisfactory evidence to the Department demonstrating that (i) immediately prior to being called to state active duty, the service member was employed and received health care coverage through his employer, (ii) the employer paid a premium to maintain the service member&#39;s health care coverage, and (iii) as a result of the service member&#39;s state active duty status, the employer is no longer paying such premium.</p><p>C. Any payment made by the Department pursuant to this section shall cover only the portion of the premium previously paid by the service member&#39;s employer from day 15 of the service member&#39;s state active duty until the date the service member is discharged from state active duty. Such payments may also cover dependents of the service member.</p><p>D. The Department may use the sum sufficient identified in the relevant state of emergency declaration for any payment authorized by this section.</p><p>E. The Department shall establish policies, procedures, and protocols to implement and record any payment authorized by this section.</p><p>F. On or before November 1, 2022, and annually thereafter, the Department shall report to the Secretary of Veterans and Defense Affairs all payments made pursuant to this section.</p><p>G. Nothing in this section shall create a legal cause of action against the Commonwealth or the Department.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0373" target="_blank" rel="noreferrer">373</a>.</p>',12),n=[o];function s(c,m,h,p,l,d){return t(),a("div",null,n)}const y=e(i,[["render",s]]);export{f as __pageData,y as default};
