import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 44-104 Care required and liability of officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-104.md","filePath":"vacode/44-104.md"}'),t={name:"vacode/44-104.md"},o=a('<h1 id="§-44-104-care-required-and-liability-of-officers" tabindex="-1">§ 44-104 Care required and liability of officers. <a class="header-anchor" href="#§-44-104-care-required-and-liability-of-officers" aria-label="Permalink to &quot;§ 44-104 Care required and liability of officers.&quot;">​</a></h1><p>All commissioned officers of the Virginia National Guard and Virginia Defense Force shall exercise the strictest care and vigilance for the preservation of the uniforms, arms, supplies, equipment and military property furnished to their several commands under the provisions of this chapter. Any officer receiving public property for military use shall be responsible for the articles so received by him; and he shall not transfer such property, or any portion thereof, to another, either as a loan or permanently, without the authority of the Adjutant General, or his duly authorized representative.</p><p>1930, p. 968; Michie Code 1942, § 2673(89); 1958, c. 393; 1984, c. 765; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>.</p>',3),n=[o];function s(l,c,f,d,h,p){return r(),i("div",null,n)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
