import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 44-123.3 Fort Pickett Police; powers; duties; functions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-123.3.md","filePath":"vacode/44-123.3.md"}'),r={name:"vacode/44-123.3.md"},a=i('<h1 id="§-44-123-3-fort-pickett-police-powers-duties-functions" tabindex="-1">§ 44-123.3 Fort Pickett Police; powers; duties; functions. <a class="header-anchor" href="#§-44-123-3-fort-pickett-police-powers-duties-functions" aria-label="Permalink to &quot;§ 44-123.3 Fort Pickett Police; powers; duties; functions.&quot;">​</a></h1><p>There is hereby established within the Department of Military Affairs the Fort Pickett Police Department. The Fort Pickett Police may exercise within the limits of the Fort Pickett Reservation and, when assigned to any other property owned or controlled by the Commonwealth or any agency, department, institution or commission thereof, all the powers, duties and functions that are exercised by the police of the city, or the police or sheriff of the county within which said property is located. The jurisdiction of the Fort Pickett Police shall further extend 300 feet beyond the boundary of any property they are required to protect, such jurisdiction to be concurrent with that of other law-enforcement officers of the locality in which such property is located. The Fort Pickett Police shall refer any complaint which alleges a felony violation to the Virginia State Police or the sheriff of the appropriate jurisdiction who, in cooperation with the Fort Pickett Police, shall conduct an investigation if an investigation is warranted. All members of the Fort Pickett Police shall be subject to the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-1202.1/" target="_blank" rel="noreferrer">2.2-1202.1</a> and Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/9.1-500/" target="_blank" rel="noreferrer">9.1-500</a> et seq.) of Title 9.1. The Fort Pickett Police Department shall be under the supervision of the Adjutant General, or his designee. The pay structure for the officers and supervisors of the Fort Pickett Police Department shall be the same as that set by the Compensation Board for sheriffs&#39; offices.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),n=[a];function s(c,h,l,f,p,d){return t(),o("div",null,n)}const P=e(r,[["render",s]]);export{_ as __pageData,P as default};
