import{_ as n,o as t,c as r,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 44-13.3 Virginia National Guard MWR Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-13.3.md","filePath":"vacode/44-13.3.md"}'),i={name:"vacode/44-13.3.md"},o=e("h1",{id:"§-44-13-3-virginia-national-guard-mwr-fund",tabindex:"-1"},[a("§ 44-13.3 Virginia National Guard MWR Fund. "),e("a",{class:"header-anchor",href:"#§-44-13-3-virginia-national-guard-mwr-fund","aria-label":'Permalink to "§ 44-13.3 Virginia National Guard MWR Fund."'},"​")],-1),d=e("p",null,'There is created within the state treasury a special nonreverting fund to be known as the Virginia National Guard MWR Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All (i) proceeds collected under the MWR Program and (ii) donations made to the Virginia National Guard MWR Program shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys from the Fund shall be used for the enhancement of morale, welfare, and recreation and the administration of the MWR Program, including paying the costs of (a) salaries of MWR Program employees; (b) public liability insurance, when needed; (c) infrastructure improvements on military property used in support of the MWR Program; and (d) any other expenses considered necessary in furtherance of the MWR Program by the Adjutant General. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Adjutant General.',-1),s=e("p",null,[a("2016, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0622",target:"_blank",rel:"noreferrer"},"622"),a(".")],-1),l=[o,d,s];function h(u,c,m,f,p,g){return t(),r("div",null,l)}const y=n(i,[["render",h]]);export{b as __pageData,y as default};
