import{_ as o,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 44-146.14 Findings of General Assembly.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-146.14.md","filePath":"vacode/44-146.14.md"}'),r={name:"vacode/44-146.14.md"},s=e("h1",{id:"§-44-146-14-findings-of-general-assembly",tabindex:"-1"},[t("§ 44-146.14 Findings of General Assembly. "),e("a",{class:"header-anchor",href:"#§-44-146-14-findings-of-general-assembly","aria-label":'Permalink to "§ 44-146.14 Findings of General Assembly."'},"​")],-1),i=e("p",null,"(a) Because of the ever present possibility of the occurrence of disasters of unprecedented size and destructiveness resulting from enemy attack, sabotage or other hostile action, resource shortage, or from fire, flood, earthquake, or other natural causes, and in order to insure that preparations of the Commonwealth and its political subdivisions will be adequate to deal with such emergencies, and generally to provide for the common defense and to protect the public peace, health, and safety, and to preserve the lives and property and economic well-being of the people of the Commonwealth, it is hereby found and declared to be necessary and to be the purpose of this chapter:",-1),c=e("p",null,"(1) To create a State Department of Emergency Management, and to authorize the creation of local organizations for emergency management in the political subdivisions of the Commonwealth;",-1),h=e("p",null,"(2) To confer upon the Governor and upon the executive heads or governing bodies of the political subdivisions of the Commonwealth emergency powers provided herein; and",-1),l=e("p",null,"(3) To provide for rendering of mutual aid among the political subdivisions of the Commonwealth and with other states and to cooperate with the federal government with respect to the carrying out of emergency service functions.",-1),d=e("p",null,"(b) It is further declared to be the purpose of this chapter and the policy of the Commonwealth that all emergency service functions of the Commonwealth be coordinated to the maximum extent possible with the comparable functions of the federal government, other states, and private agencies of every type, and that the Governor shall be empowered to provide for enforcement by the Commonwealth of national emergency services programs, to the end that the most effective preparation and use may be made of the nation's resources and facilities for dealing with any disaster that may occur.",-1),f=e("p",null,[t("1973, c. 260; 1974, c. 4; 1975, c. 11; 2000, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0309",target:"_blank",rel:"noreferrer"},"309"),t(".")],-1),m=[s,i,c,h,l,d,f];function p(u,g,v,b,_,y){return a(),n("div",null,m)}const C=o(r,[["render",p]]);export{x as __pageData,C as default};
