import{_ as n,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 44-146.29:3 Emergency Shelters Upgrade Assistance Grant Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-146.29:3.md","filePath":"vacode/44-146.29:3.md"}'),s={name:"vacode/44-146.29:3.md"},i=e("h1",{id:"§-44-146-29-3-emergency-shelters-upgrade-assistance-grant-fund",tabindex:"-1"},[t("§ 44-146.29:3 Emergency Shelters Upgrade Assistance Grant Fund. "),e("a",{class:"header-anchor",href:"#§-44-146-29-3-emergency-shelters-upgrade-assistance-grant-fund","aria-label":'Permalink to "§ 44-146.29:3 Emergency Shelters Upgrade Assistance Grant Fund."'},"​")],-1),o=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Emergency Shelters Upgrade Assistance Grant Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All funds appropriated for such purpose and any gifts, donations, grants, bequests, and other funds received on its behalf shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the purposes of providing matching funds to localities or entities previously outlined in a local shelter plan to install, maintain, or repair infrastructure for backup energy generation for emergency shelters, including solar energy generators, and improve the hazard-specific structural integrity of shelter facilities owned by the locality or identified in the shelter plan of the locality. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Director of Emergency Management or, if designated, the State Coordinator of Emergency Management.',-1),d=e("p",null,[t("2020, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0819",target:"_blank",rel:"noreferrer"},"819"),t("; 2022, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0337",target:"_blank",rel:"noreferrer"},"337"),t(".")],-1),l=[i,o,d];function h(c,u,g,p,f,m){return r(),a("div",null,l)}const _=n(s,[["render",h]]);export{b as __pageData,_ as default};
