import{_ as t,o as r,c as o,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 44-19 Adjutant General to have charge of military property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-19.md","filePath":"vacode/44-19.md"}'),i={name:"vacode/44-19.md"},n=e("h1",{id:"§-44-19-adjutant-general-to-have-charge-of-military-property",tabindex:"-1"},[a("§ 44-19 Adjutant General to have charge of military property. "),e("a",{class:"header-anchor",href:"#§-44-19-adjutant-general-to-have-charge-of-military-property","aria-label":'Permalink to "§ 44-19 Adjutant General to have charge of military property."'},"​")],-1),s=e("p",null,"The Adjutant General shall have charge and care of all state military property and all United States military property issued to the Commonwealth of Virginia, and shall cause to be kept an accurate and careful account of all receipts and issues of the same. He shall require to be kept careful memoranda of all public military property on hand in the state arsenal or storehouses, and in the possession of the several organizations of the Virginia National Guard or issued to the Virginia Defense Force, and shall guard such property against injury and loss to the extent of his ability. He shall require every accountable and responsible officer of the National Guard to account for every deficiency in public military property in his possession immediately after such deficiency is discovered. The Adjutant General shall have the care and control of the state military reservation near Virginia Beach, and of such other real estate as the Commonwealth may acquire for military purposes, and it shall be the duty of the Adjutant General to provide for the proper care of such property and buildings thereon. For the maintenance, upkeep and improvement of the military reservation or reservations, the Adjutant General may expend from the appropriation to the Department of Military Affairs such amounts as may be necessary.",-1),l=e("p",null,[a("1930, pp. 950, 952; Michie Code 1942, §§ 2673(11), 2673(13); 1944, p. 24; R. P. 1948, § 44-19; 1958, c. 393; 1989, c. 414; 2011, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572",target:"_blank",rel:"noreferrer"},"572"),a(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586",target:"_blank",rel:"noreferrer"},"586"),a(".")],-1),c=[n,s,l];function h(d,p,f,u,m,y){return r(),o("div",null,c)}const _=t(i,[["render",h]]);export{v as __pageData,_ as default};
