import{_ as i,o as n,c as o,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 44-40.01 Nonjudicial disciplinary proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-40.01.md","filePath":"vacode/44-40.01.md"}'),r={name:"vacode/44-40.01.md"},t=e("h1",{id:"§-44-40-01-nonjudicial-disciplinary-proceedings",tabindex:"-1"},[a("§ 44-40.01 Nonjudicial disciplinary proceedings. "),e("a",{class:"header-anchor",href:"#§-44-40-01-nonjudicial-disciplinary-proceedings","aria-label":'Permalink to "§ 44-40.01 Nonjudicial disciplinary proceedings."'},"​")],-1),s=e("p",null,"Service members of the Virginia National Guard, not in federal service, are subject to nonjudicial punishment pursuant to Part V of the Manual for Courts-Martial, United States. A commander's authority to impose nonjudicial punishment shall not include the authority to impose correctional custody, arrest in quarters, restriction, or extra duties. Reduction in rank shall not be imposed if the service member has more than 10 years of military service and is in the rank of E-5 or above. When nonjudicial punishment is to be imposed by a commanding officer of the rank of major or above, a service member shall not be allowed to demand trial by court-martial in lieu of nonjudicial punishment. Service members shall retain all appeal rights available under the Manual for Courts-Martial, and the appeal shall include a review for abuse of discretion and legal sufficiency of the evidence.",-1),c=e("p",null,[a("2015, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0194",target:"_blank",rel:"noreferrer"},"194"),a(".")],-1),l=[t,s,c];function d(u,h,p,m,f,_){return n(),o("div",null,l)}const g=i(r,[["render",d]]);export{v as __pageData,g as default};
