import{_ as i,o as e,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 44-41.2 Sharing of information with Virginia Employment Commission upon separation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-41.2.md","filePath":"vacode/44-41.2.md"}'),n={name:"vacode/44-41.2.md"},o=t('<h1 id="§-44-41-2-sharing-of-information-with-virginia-employment-commission-upon-separation" tabindex="-1">§ 44-41.2 Sharing of information with Virginia Employment Commission upon separation. <a class="header-anchor" href="#§-44-41-2-sharing-of-information-with-virginia-employment-commission-upon-separation" aria-label="Permalink to &quot;§ 44-41.2 Sharing of information with Virginia Employment Commission upon separation.&quot;">​</a></h1><p>The Adjutant General shall, in consultation with the Commissioner of the Virginia Employment Commission, establish a program under which the Department of Military Affairs shall, upon request of a member of the Virginia National Guard, provide information on the member to the Virginia Employment Commission, including (i) the individual&#39;s name; (ii) the date, or anticipated date, of the individual&#39;s discharge, separation, or release from the Virginia National Guard; (iii) the characterization, or anticipated characterization, of the individual&#39;s discharge from the Virginia National Guard; (iv) the individual&#39;s level of education; (v) contact information for the individual; (vi) the individual&#39;s military job classification and list of military training and certifications; (vii) the individual&#39;s civilian skills, training, and certification, as self-selected by the individual; and (viii) the industry, employment sector, profession, or other career classification, as self-identified by the individual, in which the individual will seek employment.</p><p>Nothing in this section shall be construed to limit the collection and maintenance of information for records required to be kept by the Virginia Employment Commission pursuant to <a href="http://law.lis.virginia.gov/admincode/title16/agency5/chapter32/section10/" target="_blank" rel="noreferrer">16VAC5-32-10</a> or pursuant to any other regulation adopted in accordance with § <a href="http://law.lis.virginia.gov/vacode/60.2-111/" target="_blank" rel="noreferrer">60.2-111</a>.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0042" target="_blank" rel="noreferrer">42</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0302" target="_blank" rel="noreferrer">302</a>.</p>',4),r=[o];function s(l,h,c,d,m,p){return e(),a("div",null,r)}const u=i(n,[["render",s]]);export{g as __pageData,u as default};
