import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 44-44 Special courts-martial.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-44.md","filePath":"vacode/44-44.md"}'),a={name:"vacode/44-44.md"},r=o('<h1 id="§-44-44-special-courts-martial" tabindex="-1">§ 44-44 Special courts-martial. <a class="header-anchor" href="#§-44-44-special-courts-martial" aria-label="Permalink to &quot;§ 44-44 Special courts-martial.&quot;">​</a></h1><p>A. In the National Guard for servicemembers on orders for duty under Title 32 of the United States Code or state active duty, the first commanding officer in the rank of colonel or above may convene special courts-martial.</p><p>B. A special court-martial may not try a commissioned officer.</p><p>C. A special court-martial shall have the authority to impose any of the following fines and penalties:</p><ol><li><p>A fine of not more than $1,000;</p></li><li><p>Forfeiture of two-thirds pay per month for six months;</p></li><li><p>A reprimand;</p></li><li><p>Bad Conduct discharge;</p></li><li><p>Restriction to limits;</p></li><li><p>Imposition of extra duty;</p></li><li><p>Confinement for not more than three months;</p></li><li><p>Reduction of enlisted persons one or more pay grades; or</p></li><li><p>Any combination of these punishments.</p></li></ol><p>1930, p. 956; Michie Code 1942, § 2673(34); 1958, c. 393; 1964, c. 227; 1976, c. 399; 1977, c. 74; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>.</p>',6),n=[r];function l(s,c,p,d,m,f){return t(),i("div",null,n)}const u=e(a,[["render",l]]);export{_ as __pageData,u as default};
