import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 44-5 Exemptions from militia duty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-5.md","filePath":"vacode/44-5.md"}'),a={name:"vacode/44-5.md"},s=o('<h1 id="§-44-5-exemptions-from-militia-duty" tabindex="-1">§ 44-5 Exemptions from militia duty. <a class="header-anchor" href="#§-44-5-exemptions-from-militia-duty" aria-label="Permalink to &quot;§ 44-5 Exemptions from militia duty.&quot;">​</a></h1><p>In addition to those exempted by the laws of the United States, the following persons shall be exempt from military duty under a state call:</p><ol><li><p>The officers, judicial and executive, of the governments of the United States and the Commonwealth of Virginia;</p></li><li><p>The members of the General Assembly of the Commonwealth of Virginia and of the Congress of the United States;</p></li><li><p>Persons in the active military or naval services of the United States;</p></li><li><p>Persons employed by the United States in the transmission of the mail;</p></li><li><p>The judges and clerks of courts of record;</p></li><li><p>The mayor and councilmen of incorporated cities and towns;</p></li><li><p>Members of the governing bodies of counties;</p></li><li><p>Sheriffs, United States district attorneys, attorneys for the Commonwealth and city attorneys;</p></li><li><p>Marine pilots;</p></li><li><p>All persons who because of religious belief shall claim exemption from military service, if the conscientious holding of such belief by such person shall be established under such regulations as the President of the United States shall prescribe, shall be exempted from militia service in any capacity that the President shall declare to be combatant; and</p></li><li><p>Such other persons as may be designated by the Governor in the best interests of the public and of the Commonwealth.</p></li></ol><p>1930, p. 949; Michie Code 1942, § 2673(5); 1958, c. 393; 1976, c. 399; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>.</p>',4),n=[s];function r(l,h,c,d,p,m){return t(),i("div",null,n)}const _=e(a,[["render",r]]);export{u as __pageData,_ as default};
