import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 44-53 Collection of fines.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-53.md","filePath":"vacode/44-53.md"}'),i={name:"vacode/44-53.md"},s=e("h1",{id:"§-44-53-collection-of-fines",tabindex:"-1"},[a("§ 44-53 Collection of fines. "),e("a",{class:"header-anchor",href:"#§-44-53-collection-of-fines","aria-label":'Permalink to "§ 44-53 Collection of fines."'},"​")],-1),r=e("p",null,"For the purpose of collecting any fines or penalties imposed by a court-martial, the military judge whenever one sits on such court, and otherwise the president of the court, or the summary court officer, shall, within fifteen days after the fines or penalties have been imposed and approved, make a list of all the persons fined, describing them distinctly, and showing the sums imposed as fines or penalties on each person, and shall draw his warrant, under his official signature, directed to any marshal of the court, or to the sheriff, sergeant, or any policeman of any city or county, as the case may be, thereby commanding him to levy such fines or penalties, together with the costs, on the goods and chattels of such delinquent, and the warrant shall thereupon have the force and effect of fieri facias, but such delinquent shall not be entitled to the benefit of any exemption law of this Commonwealth, as against such warrant and the lien thereof. In default of sufficient personal property to satisfy the same, the officer executing the same shall make report accordingly to the drawing authority of the warrant which may then require the fined person to show whether or not he possesses sufficient property to satisfy the fine and if such property is found to exist and the fined person fails to deliver it over the executing officer shall be ordered to take the body of the delinquent and convey him to the jail of the city or county in which he may be found, whose jailer shall closely confine him without bail until the fine or penalty and jailer's fees be paid. No such imprisonment shall extend beyond the period of ten days.",-1),h=e("p",null,"1930, p. 958; Michie Code 1942, § 2673(44); 1976, c. 399; 1977, c. 74.",-1),c=[s,r,h];function l(f,d,p,u,m,y){return o(),n("div",null,c)}const b=t(i,[["render",l]]);export{w as __pageData,b as default};
