import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 44-54.6 Members, appointment and enlistment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-54.6.md","filePath":"vacode/44-54.6.md"}'),r={name:"vacode/44-54.6.md"},i=n('<h1 id="§-44-54-6-members-appointment-and-enlistment" tabindex="-1">§ 44-54.6 Members, appointment and enlistment. <a class="header-anchor" href="#§-44-54-6-members-appointment-and-enlistment" aria-label="Permalink to &quot;§ 44-54.6 Members, appointment and enlistment.&quot;">​</a></h1><p>The age limitations of § <a href="/vacode/44-1/">44-1</a> to the contrary notwithstanding, the Virginia Defense Force shall consist of:</p><ol><li><p>Such volunteers who of their own volition agree to service in conformity with regulations prescribed by the Adjutant General who are (i) residents of the Commonwealth or any contiguous state, (ii) at least 16, provided that any volunteer under the age of 18 shall have the written consent of at least one parent or guardian, and (iii) less than 65 years of age may join the Virginia Defense Force, except that the Adjutant General may, on a case-by-case basis, authorize volunteer members of the Virginia Defense Force to be accessed or retained beyond the age of 65 to the age of 75.</p></li><li><p>Such persons of the unorganized militia who may be drafted to fill the force structure of the Virginia Defense Force or who may be ordered out for active duty until released from such service.</p></li></ol><p>The officers of the Virginia Defense Force shall be appointed by the Governor in conformity with regulations prescribed by the Adjutant General.</p><p>Enlisted members shall be enlisted and retained in conformity with regulations prescribed by the Adjutant General.</p><p>1989, c. 414; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>.</p>',6),o=[i];function s(l,c,h,d,f,p){return t(),a("div",null,o)}const u=e(r,[["render",s]]);export{g as __pageData,u as default};
