import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 44-93.4 Discrimination against persons who serve in the Virginia National Guard, Virginia Defense Force, or National Guard of another state and acts of reprisal prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-93.4.md","filePath":"vacode/44-93.4.md"}'),o={name:"vacode/44-93.4.md"},t=r('<h1 id="§-44-93-4-discrimination-against-persons-who-serve-in-the-virginia-national-guard-virginia-defense-force-or-national-guard-of-another-state-and-acts-of-reprisal-prohibited" tabindex="-1">§ 44-93.4 Discrimination against persons who serve in the Virginia National Guard, Virginia Defense Force, or National Guard of another state and acts of reprisal prohibited. <a class="header-anchor" href="#§-44-93-4-discrimination-against-persons-who-serve-in-the-virginia-national-guard-virginia-defense-force-or-national-guard-of-another-state-and-acts-of-reprisal-prohibited" aria-label="Permalink to &quot;§ 44-93.4 Discrimination against persons who serve in the Virginia National Guard, Virginia Defense Force, or National Guard of another state and acts of reprisal prohibited.&quot;">​</a></h1><p>A. A member of the Virginia National Guard or Virginia Defense Force, or a person who is a member of the National Guard of another state, who performs, has performed, applies to perform, or has an obligation to perform state active duty or military duty pursuant to Title 32 of the United States Code shall not be denied initial employment, reemployment, retention in employment, promotion, or any benefit of employment by an employer within the Commonwealth on the basis of that membership, application for membership, performance of service, application for service, or obligation.</p><p>B. A person shall be considered to have denied a member of the Virginia National Guard or Virginia Defense Force, or a person who is a member of the National Guard of another state, initial employment, reemployment, retention in employment, promotion, or a benefit of employment within the Commonwealth in violation of this section if the member&#39;s membership, application for membership, performance of service, application for service, or obligation for service is a motivating factor in that person&#39;s action, unless the person can prove by the greater weight of the evidence that the same unfavorable action would have taken place in the absence of the member&#39;s membership, application for membership, performance of service, application for service, or obligation for service.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0167" target="_blank" rel="noreferrer">167</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0327" target="_blank" rel="noreferrer">327</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>.</p>',4),n=[t];function s(l,p,f,h,c,m){return i(),a("div",null,n)}const b=e(o,[["render",s]]);export{d as __pageData,b as default};
