import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 44-93.5 Penalties for denial.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/44-93.5.md","filePath":"vacode/44-93.5.md"}'),i={name:"vacode/44-93.5.md"},o=t('<h1 id="§-44-93-5-penalties-for-denial" tabindex="-1">§ 44-93.5 Penalties for denial. <a class="header-anchor" href="#§-44-93-5-penalties-for-denial" aria-label="Permalink to &quot;§ 44-93.5 Penalties for denial.&quot;">​</a></h1><p>If any employer fails or refuses to comply with the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/44-93/" target="_blank" rel="noreferrer">44-93</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.2/" target="_blank" rel="noreferrer">44-93.2</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.3/" target="_blank" rel="noreferrer">44-93.3</a> and <a href="http://law.lis.virginia.gov/vacode/44-93.4/" target="_blank" rel="noreferrer">44-93.4</a>, the circuit court having jurisdiction over the employer&#39;s place of business may, upon the filing of a motion, petition, or other appropriate pleading by the employee, require the employer to comply with §§ <a href="http://law.lis.virginia.gov/vacode/44-93/" target="_blank" rel="noreferrer">44-93</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.2/" target="_blank" rel="noreferrer">44-93.2</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.3/" target="_blank" rel="noreferrer">44-93.3</a> and <a href="http://law.lis.virginia.gov/vacode/44-93.4/" target="_blank" rel="noreferrer">44-93.4</a> and to compensate the employee for any loss of wages or benefits and reasonable attorney fees and costs incurred by reason of the employer&#39;s unlawful failure or refusal. Upon request of the affected employee, the Attorney General may represent personally or through one of his assistants, such employee denied the benefits of §§ <a href="http://law.lis.virginia.gov/vacode/44-93/" target="_blank" rel="noreferrer">44-93</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.2/" target="_blank" rel="noreferrer">44-93.2</a>, <a href="http://law.lis.virginia.gov/vacode/44-93.3/" target="_blank" rel="noreferrer">44-93.3</a> and <a href="http://law.lis.virginia.gov/vacode/44-93.4/" target="_blank" rel="noreferrer">44-93.4</a> while in the performance of state active duty.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0147" target="_blank" rel="noreferrer">147</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0167" target="_blank" rel="noreferrer">167</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>.</p>',3),n=[o];function l(s,f,h,g,p,c){return r(),a("div",null,n)}const _=e(i,[["render",l]]);export{d as __pageData,_ as default};
