import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-1009  Permit required for coal surface mining operation; term; transfer, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1009.md","filePath":"vacode/45.2-1009.md"}'),i={name:"vacode/45.2-1009.md"},o=a('<h1 id="§-45-2-1009-permit-required-for-coal-surface-mining-operation-term-transfer-etc" tabindex="-1">§ 45.2-1009 Permit required for coal surface mining operation; term; transfer, etc. <a class="header-anchor" href="#§-45-2-1009-permit-required-for-coal-surface-mining-operation-term-transfer-etc" aria-label="Permalink to &quot;§ 45.2-1009  Permit required for coal surface mining operation; term; transfer, etc.&quot;">​</a></h1><p>A. No person shall engage in or carry out any coal surface mining operation without having first obtained a permit to engage in such operation issued by the Director in accordance with state regulations.</p><p>B. Each coal surface mining permit issued pursuant to the requirements of this chapter shall be for a term of five years. The rights granted under such permit shall not be transferred, assigned, or sold without the written approval of the Director in accordance with regulations adopted by the Director. The Director shall also adopt regulations meeting the requirements of § 506 of the federal act for longer permit terms, successors in interest to the permittee, termination of the permit for failure to commence operation, right of and procedure for permit renewal, and extension of boundaries of a mining operation.</p><p>1979, c. 290, § 45.1-234; 1980, c. 364; 1983, c. 134; 1984, c. 590; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0129" target="_blank" rel="noreferrer">129</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),n=[o];function s(c,f,l,p,h,m){return r(),t("div",null,n)}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
