import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1049  Forfeiture of bonds on operations participating in the Fund; alternative remedies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1049.md","filePath":"vacode/45.2-1049.md"}'),r={name:"vacode/45.2-1049.md"},n=i('<h1 id="§-45-2-1049-forfeiture-of-bonds-on-operations-participating-in-the-fund-alternative-remedies" tabindex="-1">§ 45.2-1049 Forfeiture of bonds on operations participating in the Fund; alternative remedies. <a class="header-anchor" href="#§-45-2-1049-forfeiture-of-bonds-on-operations-participating-in-the-fund-alternative-remedies" aria-label="Permalink to &quot;§ 45.2-1049  Forfeiture of bonds on operations participating in the Fund; alternative remedies.&quot;">​</a></h1><p>A. Forfeiture of bonds of an operation participating in the Fund shall be accomplished as set forth in § <a href="/vacode/45.2-1023/">45.2-1023</a> and the regulations adopted by the Director.</p><p>B. In addition to forfeiture, the Director may proceed against the permittee of a surface coal mining operation under the provisions of subsection F of § <a href="/vacode/45.2-1020/">45.2-1020</a> by filing a civil action for injunctive or other relief in any court of competent jurisdiction to compel the permittee to perform the reclamation work in full compliance with this chapter, the regulations, and approved permit plans. Any injunctive relief shall be granted without the necessity of pleading or proving inadequate remedy at law or irreparable harm, and no bond shall be required.</p><p>C. Proceedings under either subsection A or B shall not constitute a waiver by the Director to proceed under the other subsection, nor shall the commencement of action under one subsection constitute an election to proceed solely under that subsection.</p><p>1987, c. 468, § 45.1-270.5:1; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),a=[n];function s(c,d,p,l,h,f){return t(),o("div",null,a)}const _=e(r,[["render",s]]);export{m as __pageData,_ as default};
