import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1104  Persons permitted to work in mines; age requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1104.md","filePath":"vacode/45.2-1104.md"}'),n={name:"vacode/45.2-1104.md"},o=a('<h1 id="§-45-2-1104-persons-permitted-to-work-in-mines-age-requirements" tabindex="-1">§ 45.2-1104 Persons permitted to work in mines; age requirements. <a class="header-anchor" href="#§-45-2-1104-persons-permitted-to-work-in-mines-age-requirements" aria-label="Permalink to &quot;§ 45.2-1104  Persons permitted to work in mines; age requirements.&quot;">​</a></h1><p>A. No person under 18 years of age shall be permitted to work in any mine.</p><p>B. The Department shall conform to § 212 of the federal Fair Labor Standards Act, 29 U.S.C. § 201 et seq., and federal regulations adopted pursuant to that Act with respect to any person under 18 years of age working around any mine.</p><p>C. No operator, agent, or mine foreman shall make a false statement as to the age of any person under 18 years of age applying for work in or around any mine.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:5; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),i=[o];function s(p,l,d,m,c,_){return r(),t("div",null,i)}const h=e(n,[["render",s]]);export{g as __pageData,h as default};
