import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-1106  Safety materials and supplies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1106.md","filePath":"vacode/45.2-1106.md"}'),i={name:"vacode/45.2-1106.md"},s=r('<h1 id="§-45-2-1106-safety-materials-and-supplies" tabindex="-1">§ 45.2-1106 Safety materials and supplies. <a class="header-anchor" href="#§-45-2-1106-safety-materials-and-supplies" aria-label="Permalink to &quot;§ 45.2-1106  Safety materials and supplies.&quot;">​</a></h1><p>It is the duty of each operator or agent to keep on hand at all times at each mine, or within convenient distance of each mine, a sufficient quantity of all materials and supplies required to preserve the safety of miners working in any area in which the operator is responsible for their health and safety, as required by the Act. If for any reason the operator or agent cannot procure the necessary materials or supplies, he shall cause all miners to withdraw from the mine, or from the affected portion of the mine, until such materials or supplies are received.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:7; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0695" target="_blank" rel="noreferrer">695</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),n=[s];function o(l,p,c,h,f,d){return a(),t("div",null,n)}const u=e(i,[["render",o]]);export{m as __pageData,u as default};
