import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-1109  Appointment and general qualifications of mine inspectors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1109.md","filePath":"vacode/45.2-1109.md"}'),r={name:"vacode/45.2-1109.md"},n=t('<h1 id="§-45-2-1109-appointment-and-general-qualifications-of-mine-inspectors" tabindex="-1">§ 45.2-1109 Appointment and general qualifications of mine inspectors. <a class="header-anchor" href="#§-45-2-1109-appointment-and-general-qualifications-of-mine-inspectors" aria-label="Permalink to &quot;§ 45.2-1109  Appointment and general qualifications of mine inspectors.&quot;">​</a></h1><p>A. Each mine inspector shall be appointed by the Director.</p><p>B. Each mine inspector shall (i) be at least 25 years of age; (ii) be of good moral character and temperate habits; (iii) hold a certificate as a mine foreman; and (iv) hold a certificate as a mine inspector issued prior to July 1, 2012, by the Board of Mineral Mining Examiners or on or after July 1, 2012, by the Department.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, §§ 45.1-161.292:10, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>.1-161.292:11; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[n];function l(s,c,p,f,g,_){return a(),i("div",null,o)}const m=e(r,[["render",l]]);export{h as __pageData,m as default};
