import{_ as e,o as n,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 45.2-1117  Examination fees; Mineral Mining Examiners' Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1117.md","filePath":"vacode/45.2-1117.md"}`),r={name:"vacode/45.2-1117.md"},i=a('<h1 id="§-45-2-1117-examination-fees-mineral-mining-examiners-fund" tabindex="-1">§ 45.2-1117 Examination fees; Mineral Mining Examiners&#39; Fund. <a class="header-anchor" href="#§-45-2-1117-examination-fees-mineral-mining-examiners-fund" aria-label="Permalink to &quot;§ 45.2-1117  Examination fees; Mineral Mining Examiners&#39; Fund.&quot;">​</a></h1><p>A. A fee of $10 shall be paid to the Director by each person examined before the commencement of the examination.</p><p>B. There is hereby created in the state treasury a special nonreverting fund to be known as the Mineral Mining Examiners&#39; Fund, referred to in this section as &quot;the Fund.&quot; The Fund shall be established on the books of the Comptroller. All fees collected pursuant to subsection A, together with moneys collected pursuant to § <a href="/vacode/45.2-1119/">45.2-1119</a>, shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund.</p><p>C. The Fund shall be administered by the Director, and moneys in the Fund shall be used solely for the purposes of payment of the cost of printing certificates and other necessary forms and the incidental expenses incurred by the Department in conducting examinations, reviewing examination papers, and conducting its other duties pursuant to this article. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Director. The Director shall keep accounts and records concerning the receipts and expenditures of the Fund as required by the Auditor of Public Accounts.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:22; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),o=[i];function s(d,l,h,c,u,p){return n(),t("div",null,o)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
