import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-1119  Renewal of certificates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1119.md","filePath":"vacode/45.2-1119.md"}'),i={name:"vacode/45.2-1119.md"},n=r('<h1 id="§-45-2-1119-renewal-of-certificates" tabindex="-1">§ 45.2-1119 Renewal of certificates. <a class="header-anchor" href="#§-45-2-1119-renewal-of-certificates" aria-label="Permalink to &quot;§ 45.2-1119  Renewal of certificates.&quot;">​</a></h1><p>The holder of any certificate issued by the Board of Mineral Mining Examiners or the Department, other than a general mineral miner certificate, may renew the certificate by successfully completing the examination for the renewal of such certificate. The Department shall establish requirements for renewal of a certificate in accordance with the procedure set forth in § <a href="/vacode/45.2-1115/">45.2-1115</a>. The Department shall notify a certificate holder at least 180 days prior to the expiration of the certificate. Any certificate requiring renewal that is not renewed by the fifth anniversary of its issuance or of a previous renewal is invalid. As a condition to renewal, the holder shall provide the Department with all administrative information reasonably required and pay the examination fee as provided in § <a href="/vacode/45.2-1117/">45.2-1117</a>.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:25; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),o=[n];function c(l,s,f,h,d,p){return a(),t("div",null,o)}const m=e(i,[["render",c]]);export{g as __pageData,m as default};
