import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-1122  General mineral miner certification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1122.md","filePath":"vacode/45.2-1122.md"}'),n={name:"vacode/45.2-1122.md"},t=i('<h1 id="§-45-2-1122-general-mineral-miner-certification" tabindex="-1">§ 45.2-1122 General mineral miner certification. <a class="header-anchor" href="#§-45-2-1122-general-mineral-miner-certification" aria-label="Permalink to &quot;§ 45.2-1122  General mineral miner certification.&quot;">​</a></h1><p>A. Every person beginning work in a mineral mine subsequent to January 1, 1997, shall hold a general mineral miner certificate issued by the Board of Mineral Mining Examiners or the Department. Any person who has worked in a mineral mine in the Commonwealth prior to that date may, but shall not be required to, hold a general mineral miner certificate.</p><p>B. Each applicant for a general mineral miner certificate shall prove to the Department that he has knowledge of first aid practices and has a general working knowledge of the provisions of the Act and applicable regulations pertaining to mineral mining health and safety.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:28; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),o=[t];function l(c,s,h,p,m,f){return a(),r("div",null,o)}const d=e(n,[["render",l]]);export{_ as __pageData,d as default};
