import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-1124 License required for operation of mineral mines; term.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1124.md","filePath":"vacode/45.2-1124.md"}'),n={name:"vacode/45.2-1124.md"},t=i('<h1 id="§-45-2-1124-license-required-for-operation-of-mineral-mines-term" tabindex="-1">§ 45.2-1124 License required for operation of mineral mines; term. <a class="header-anchor" href="#§-45-2-1124-license-required-for-operation-of-mineral-mines-term" aria-label="Permalink to &quot;§ 45.2-1124 License required for operation of mineral mines; term.&quot;">​</a></h1><p>A. No person shall engage in the operation of any mineral mine within the Commonwealth without first obtaining a license from the Department. Licenses shall be in a form that the Director prescribes. The license for each mine shall be posted in a conspicuous place near the main entrance to such mine.</p><p>B. A license is required prior to commencement of the operation of a mine, and a separate license shall be secured for each mine operated. The Director may transfer a license to a successor operator so long as the successor operator has complied with the requirements of the Act. Every change in ownership of a mine shall be reported to the Department as provided in subsection D of § <a href="/vacode/45.2-1129/">45.2-1129</a>.</p><p>C. Each license shall be valid for a period of one year following the date of issuance, and a mine operator shall secure the renewal of a license by its anniversary date.</p><p>D. Within 30 days after the occurrence of any change in the information required by subsection B, the licensed operator shall notify the Department in writing of such change.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:30; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0695" target="_blank" rel="noreferrer">695</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0974" target="_blank" rel="noreferrer">974</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),o=[t];function s(c,l,h,p,f,m){return r(),a("div",null,o)}const u=e(n,[["render",s]]);export{_ as __pageData,u as default};
