import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse(`{"title":"§ 45.2-1143  Workers' compensation; liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1143.md","filePath":"vacode/45.2-1143.md"}`),a={name:"vacode/45.2-1143.md"},i=o('<h1 id="§-45-2-1143-workers-compensation-liability" tabindex="-1">§ 45.2-1143 Workers&#39; compensation; liability. <a class="header-anchor" href="#§-45-2-1143-workers-compensation-liability" aria-label="Permalink to &quot;§ 45.2-1143  Workers&#39; compensation; liability.&quot;">​</a></h1><p>A. For the purpose of workers&#39; compensation coverage during any mine disaster to which a state-designated mine rescue team responds under the provisions of this article, each member of the state-designated team shall be deemed to be within the employment of the licensed operator of the mine at which the disaster occurred.</p><p>B. No member of a state-designated team engaging in rescue work at a mine shall be liable for civil damages for acts or omissions resulting from the rendering of such rescue work unless the act or omission was the result of gross negligence or willful misconduct.</p><p>C. No operator providing personnel to a state-designated mine rescue team to engage in rescue work at a mine not owned or operated by the operator shall be liable for any civil damages for acts or omissions resulting from the rendering of such rescue work.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:49; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0695" target="_blank" rel="noreferrer">695</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),s=[i];function n(l,c,d,p,h,m){return r(),t("div",null,s)}const _=e(a,[["render",n]]);export{f as __pageData,_ as default};
