import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-1147  Duties of mine inspectors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-1147.md","filePath":"vacode/45.2-1147.md"}'),s={name:"vacode/45.2-1147.md"},n=i('<h1 id="§-45-2-1147-duties-of-mine-inspectors" tabindex="-1">§ 45.2-1147 Duties of mine inspectors. <a class="header-anchor" href="#§-45-2-1147-duties-of-mine-inspectors" aria-label="Permalink to &quot;§ 45.2-1147  Duties of mine inspectors.&quot;">​</a></h1><p>Each mine inspector shall:</p><ol><li><p>Report to his supervisor immediately, and by the quickest available means, any mine fire, mine explosion, or accident involving serious personal injury or death;</p></li><li><p>Proceed immediately to the scene of any accident at any mine under his jurisdiction that results in loss of life or serious personal injury, and to the scene of any mine fire or explosion regardless of whether there is loss of life or personal injury. He shall make any investigation and suggestions and render any assistance he deems necessary for the future safety of the employees, and he shall make a complete report to his supervisor as soon as practicable. He shall have the power to compel the attendance of witnesses and administer oaths or affirmations; and</p></li><li><p>Take charge of mine rescue and recovery operations whenever a mine fire, mine explosion, or other serious accident occurs and supervise the reopening of any mine or section thereof that has been sealed or abandoned on account of fire or any other cause.</p></li></ol><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 45.1-161.292:53; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),t=[n];function r(c,l,p,d,h,f){return o(),a("div",null,t)}const _=e(s,[["render",r]]);expor